struct vertex * vertex_ek_alloc(void)
{
    struct vertex * v = (struct vertex *)malloc(sizeof(struct vertex_ek));
    if (!v) {
        error("E-K vertex allocation failed");
        exit (-1);
    }
    return v;
}

struct edge * edge_ek_alloc(void)
{
    struct edge * e = (struct edge *)malloc(sizeof(struct edge_ek));
    if (!e) {
        error("E-K edge allocation failed");
        exit (-1);
    }
    return e;
}

